import requests
import os


def save_img(self, img_name):
    img_path = "D:/test/img"
    if not os.path.exists(img_path):
        os.makedirs(img_path)
    self.browser.get_screenshot_as_file(
        '{}/{}.png'.format(os.path.abspath(img_path), img_name))

if __name__ == '__main__':
    headers = {
        "user-agent": "Mozilla/5.0 (Macintosh; Intel Mac OS X 10_14_6) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/76.0.3809.87 Safari/537.36"
    }
    session = requests.session()
    url="data:image/png;base64,"
    resp = session.get(url, headers=headers)
    # 将验证码保证到本地
    with open("testa.jpg", 'wb') as f:
        f.write(resp.content)